import TextInput from './Components/policies/TextInput'
import FileUpload from './Components/policies/FileUpload';
import StageFiveFormSchema from './Components/policies/StageFiveFormSchema';
import CustomButton from './Components/CustomButton'

export const useStyles: any = (theme: any) => ({
    accordionWrapper: {
        width: '100%',
        padding: '0px 15px',
        '& .MuiAccordion-root': {
            backgroundColor: '#fff',
        }
    },
    childAccordion: {
        width: '100%',
        '&>div': {
            boxShadow: 'none',
            width: '100%',
            border: '1px solid #ccc',
            margin: '15px 0px'
            //    padding: '15px',
        }
    },
    lineSaprator: {
        margin: '30px 0px',
    },
    errorstyle: {
        border: '1px solid red !important'
    },
    fileInput: {
        '& fieldset': {
            borderStyle: 'dashed',
        }
    },
    radioFormControl: {
        display: 'block',
    },
    flexDirection:{
        flexDirection: 'column'
    },
    BtnGroup: {
        display: 'flex',
        alignItems: 'center',
        justifyContent: 'space-between',
        padding: '0px 15px'
    },
    buttonsRightWrapper:{
        display: 'flex',
        gap: '15px',
        padding: '15px 0px',
        justifyContent: 'flex-end'
    }

})

const useStylesFunction = makeStyles({
    accordianHeading: {
        fontWeight: 600,
        display: 'flex',
        alignItems: 'center',
    },
    lockedAccordion: {
        padding: '6px 10px',
        backgroundColor: '#e9f4f9',
        border: '1px solid #ccc',
        borderRadius: '4px',
        display: 'flex',
        alignItems: 'center',
        justifyContent: 'center',
        margin: '0px 8px',
        '& svg': {
            marginRight: '6px',
        }
    },
    errorstyle: {
        border: '1px solid red !important'
    },

})

const enumYesNO = {
    YES: 'YES',
    NO: 'NO',
}

const enumTawuniya = {
