import { Accordion, AccordionSummary, AccordionDetails, Typography, makeStyles, withStyles, Grid, TextField, FormControl, FormControlLabel, FormLabel, Radio, RadioGroup } from '@material-ui/core'
import { ExpandMore } from '@material-ui/icons'
import React, { Component } from 'react'
import InsurerOverview from './Components/Insurer/InsurerOverview.web'
import LockIcon from '@material-ui/icons/Lock';
import PropTypes from 'prop-types';
import LabelWithIcon from './Components/LabelWithIcon';
import { Formik } from 'formik';
import * as Yup from "yup";
import TextInput from './Components/policies/TextInput'
import FileUpload from './Components/policies/FileUpload';
import SalesReviewFormSchema from './Components/policies/SalesReviewFormSchema';

export const useStyles: any = (theme: any) => ({
    childAccordion: {
        width: '100%',
        '&>div': {
            boxShadow: 'none',
            width: '100%',
            border: '1px solid #ccc',
            //    padding: '15px',
        }
    },
    lineSaprator: {
        margin: '30px 0px',
    }
})

const useStylesFunction = makeStyles({
    accordianHeading: {
        fontWeight: 600,
        display: 'flex',
        alignItems: 'center',
    },
    lockedAccordion: {
        padding: '6px 10px',
        backgroundColor: '#e9f4f9',
        border: '1px solid #ccc',
        borderRadius: '4px',
        display: 'flex',
        alignItems: 'center',
        justifyContent: 'center',
        margin: '0px 8px',
        '& svg': {
            marginRight: '6px',
        }
    }

})

const enumYesNO = {
    YES: 'YES',
    NO: 'NO',
}

type accordionTitleProps = {
    title: string,
    // insuranceData: any,
}

const AccordionTitle = (props: accordionTitleProps) => {
    const classes = useStylesFunction()
    return (
        <Typography className={classes.accordianHeading}>
            {props?.title}
            {/* {
                    <div className={classes.lockedAccordion}>
                        <LockIcon /> Please select insurance type to unlock  this fields
                    </div>
            } */}

        </Typography>
    )
}

type MyState = {
    isEdit: boolean,
    tawuniya: any,
    saudiBank: any,
    axaBank: any,
    release_premium_calculation: string,
    comparison_sheet: string,
    insurance_company_id: string,
    isArabicLanguage: boolean,
};

type MyProps = {
    params: any,
    formType: string,
    changeEdit: () => void,
};

class SalesReview extends Component<MyProps, MyState> {
    static propTypes: { classes: any; };
    constructor(props: any) {
        super(props);
        // console.log(this.props.match.params.id)
        // this.handleStorage = this.handleStorage.bind(this);
        this.state = {
            isEdit: false,
            isArabicLanguage: false,
            tawuniya: {
                quotation_recived: '',
                quotation_amount: '',
                comment: '',
                proposal: null,
                additional_attachment: null,
                note_to_the_customer: '',
                release_quotation_to_customer: '',
            },
            saudiBank: {
                quotation_recived: '',
                comment: '',
                additional_attachment: null,
                release_quotation_to_customer: '',
                note_to_the_customer: '',
            },
            axaBank: {
                quotation_recived: '',
                quotation_amount: '',
                comment: '',
                proposal: null,
                additional_attachment: null,
                note_to_the_customer: '',
                release_quotation_to_customer: '',
            },
            release_premium_calculation: '',
            comparison_sheet: '',
            insurance_company_id: '',
        };
    }



    render() {
        const { classes }: any = this.props;
        return (
            <div>
                <Formik
                    enableReinitialize={this.state.isEdit ? true : false}
                    initialValues={{
                        quotation_amount: '',
                            tawuniya_quotation_recived: '',
                            tawuniya_quotation_amount: '',
                            tawuniya_comment: '',
                            tawuniya_proposal: null,
                            tawuniya_additional_attachment: null,
                            tawuniya_note_to_the_customer: '',
                            tawuniya_release_quotation_to_customer: '',
                            saudiBank_quotation_recived: '',
                            saudiBank_comment: '',
                            saudiBank_additional_attachment: null,
                            saudiBank_release_quotation_to_customer: '',
                            saudiBank_note_to_the_customer: '',
                            axaBank_quotation_recived: '',
                            axaBank_quotation_amount: '',
                            axaBank_comment: '',
                            axaBank_proposal: null,
                            axaBank_additional_attachment: null,
                            axaBank_note_to_the_customer: '',
                            axaBank_release_quotation_to_customer: '',
                        release_premium_calculation: '',
                        comparison_sheet: '',
                        insurance_company_id: '',
                    }}
                    validationSchema={Yup.object().shape(SalesReviewFormSchema(this.state.isArabicLanguage))}
                    // validateOnMount={true}
                    // validateOnBlur={true}
